import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../environments/environment';
@Injectable({
  providedIn: 'root'
})
export class ApiService {
  // External IP from your LoadBalancer
  private baseIp = 'http://34.67.95.206';

  constructor(private http: HttpClient) {}

  // Auth Endpoints (port 5000)
  login(credentials: { username: string; password: string }): Observable<any> {
    return this.http.post(`${this.baseIp}:5000/login`, credentials);
  }

  register(credentials: { username: string; password: string }): Observable<any> {
    return this.http.post(`${this.baseIp}:5000/register`, credentials);
  }

  // Sensor Data Endpoints (port 3000)
  postSensorData(sensor: string, value: number): Observable<any> {
    return this.http.post(`${this.baseIp}:3000/data`, { sensor, value });
  }

  getSensorData(): Observable<any> {
    return this.http.get(`${this.baseIp}:3000/data`);
  }

  // Notifications Endpoint (port 8080)
  sendNotification(message: string): Observable<any> {
    return this.http.post(`${this.baseIp}:8080/notify`, { message });
  }

  // SSE subscription (port 4000)
  subscribeToNotifications(): Observable<string> {
    return new Observable<string>((observer) => {
      const eventSource = new EventSource(`${this.baseIp}:4000/subscribe`);
      eventSource.onmessage = (event: MessageEvent) => {
        observer.next(event.data);
      };
      eventSource.onerror = (error) => {
        observer.error(error);
        eventSource.close();
      };
      return () => {
        eventSource.close();
      };
    });
  }
}
