import { Component, OnInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ApiService } from '../api.service';
import { Subscription } from 'rxjs';

@Component({
  standalone: true,
  selector: 'app-notifications',
  imports: [CommonModule],
  templateUrl: './notifications.component.html',
  styleUrls: ['./notifications.component.css']
})
export class NotificationsComponent implements OnInit, OnDestroy {
  notifications: string[] = [];
  sub?: Subscription;

  constructor(private api: ApiService) {}

  ngOnInit() {
    this.sub = this.api.subscribeToNotifications().subscribe({
      next: msg => this.notifications.push(msg),
      error: err => this.notifications.push('Error receiving updates')
    });
  }

  ngOnDestroy() {
    this.sub?.unsubscribe();
  }
}
<h2 class="mb-3">Real-Time Notifications</h2>
<div *ngIf="notifications.length === 0" class="text-muted">
  No notifications received yet.
</div>
<ul class="list-group" *ngIf="notifications.length > 0">
  <li class="list-group-item" *ngFor="let msg of notifications">
    {{ msg }}
  </li>
</ul>
